from slackbot.bot import listen_to
from slackbot.bot import respond_to
import re
import returnTicket as bugz

@listen_to('!ticket (\d{4,6})')
@listen_to('!ticekt (\d{4,6})')
@listen_to('!case (\d{4,6})')
@listen_to('!bugz (\d{4,6})')
@listen_to('!bugzid (\d{4,6})')
def ticket(message, ticketNum):
	ticket = bugz.returnTicket(ticketNum)
	message.send(ticket)
